const tmpPL = {};
for(const channel of msg.payload){
    const subs = channel.common.name.split(' ');
    const type = subs[0] ? subs[0] : ''
    if((subs[0] !== 'Channel' && ['Storen', 'Lichtschalter'].includes(type) )){
        tmpPL[`${channel._id}`] = {
            id:channel._id,
            name:channel.common.name,
            adapterName:'Shelly',
            channelType:type,
            room:subs[1] ? subs[1] : '',
            place:subs[2] ? subs[2] : '',
            type:type,
            info:subs.join(" "),
            scenes: (type === 'Storen')
                ? [
                    {
                        id:channel._id + '.Open',
                        name:'Open'
                    },
                    {
                        id:channel._id + '.Stop',
                        name:'Stop'
                    },
                    {
                        id:channel._id + '.Close',
                        name:'Close'
                    }
                ]
                : []
        }
    }
}
msg.payload = tmpPL
return msg;

msg.topic = msg.mapping.consumerIOBrokerChannelPath + '.Switch';
msg.payload = !msg.result;
return msg

msg.topic = msg.mapping.consumerIOBrokerStatePath;
msg.payload = true;
return msg

