    const msg1 = {}
    msg1.topic = selectedLampeID + '.on'
    msg1.payload = true
    msg.topic = selectedSceneID
    msg.payload = true
    return [msg, msg1];
    //return msg;
}

if(msg.result){
    return switchOff(msg.mapping.consumerIOBrokerChannelPath);
}
return (msg.mapping.consumerIOBrokerStatePath)
    ? switchScene(msg.mapping.consumerIOBrokerChannelPath, msg.mapping.consumerIOBrokerStatePath) 
    : switchOn(msg.mapping.consumerIOBrokerChannelPath);

