const tmpPL = {};
for(const channel of msg.payload){
    const subs = channel.common.name.split(' ');
    if(channel.common && typeof channel.common.name === 'string' && channel.common.name.startsWith('OnOff'))
    tmpPL[`${channel._id}`] = {
