    msg.topic = consumerIOBrokerChannelPath + '.capabilities.switch-off';
    msg.payload = true;
    return msg;
}

const switchOn = (consumerIOBrokerChannelPath) => {
    msg.topic = consumerIOBrokerChannelPath + '.capabilities.switch-on';
    msg.payload = true;
    return msg;
}

msg.result === "on" 
    ? switchOff(msg.mapping.consumerIOBrokerChannelPath)
    : switchOn(msg.mapping.consumerIOBrokerChannelPath)

return msg;

let matchArray = []

const request = (Array.isArray(msg.request)) ? msg.request[0] : msg.request

if(msg.payload && 
        Array.isArray(msg.payload) && 
        msg.payload.length > 0)
{
    if(request.hasOwnProperty('id')){
        matchArray = msg.payload.filter(e => 
            e.hasOwnProperty('styleButtonIOBrokerStatePath') &&
            e.styleButtonIOBrokerStatePath.split('&%&')[0] === request.id
        );
    }
    if(request.hasOwnProperty('channelID') && request.hasOwnProperty('style')){
        const channelID = request.channelID.replaceAll('___', '#');
        matchArray = msg.payload.filter(e => 
            e.hasOwnProperty('switchIOBrokerChannelPath') &&
            e.hasOwnProperty('style') &&
            e['switchIOBrokerChannelPath'] === channelID &&
            e['style'] === request['style']
        );
    }
}
msg.payload = matchArray
return msg;

const t = flow.get('$parent.ConfigFunctions').find(e => e.ConfigFunctionName === msg.mapping.configType);
if(t) {
    msg.consumers = t.consumers;
    msg.switches = t.switches;
    msg.filter = t.filter;
    msg.ConfigFunctionName = t.ConfigFunctionName;
}

return msg;

