return msg;

const _http_tcp = flow.get('_http_tcp') ?? {};
const shelly = _http_tcp[msg.payload.name] ?? {};

shelly.name = msg.payload.name
shelly.ip = msg.payload.addresses[0]
shelly.port = msg.payload.port
shelly.protocol = msg.payload.type.name
shelly.url = `${shelly.protocol}://${shelly.ip}:${shelly.port }`
shelly.gen = msg.payload.txtRecord.gen ?? "1"
shelly.org = msg.payload

_http_tcp[msg.payload.addresses[0]] = shelly;
flow.set('_http_tcp', _http_tcp);

return msg;

msg.url = msg.Shelly._http_tcp.url + '/rpc/Shelly.Update';
msg.payload = {
    stage: 'stable'
}
return msg;

let ui_data;
if (msg.Shelly.config.hasOwnProperty('switch:1')){
    ui_data = '{"consumption_types":["lights", "lights"]}';
} else if (msg.Shelly.config.hasOwnProperty('switch:0')) {
    ui_data = '{"consumption_types":["lights"]}';
} else if (msg.Shelly.config.hasOwnProperty('cover:0')) {
    ui_data = '{}';
}

msg.url = `${msg.Shelly._http_tcp.url}/rpc/Sys.SetConfig?config={"ui_data":${ui_data}}`;
return msg;

const channelID = `${flow.get('shellyInstance')}.${msg.Shelly._http_tcp.name.toLowerCase().replace('-', '___')}___1.Relay${msg.channelID}`;

const hookTypes = msg.payload.hook_types;
msg.url = `${msg.Shelly._http_tcp.url}/rpc`;

msg.payload = []

for(const e of hookTypes) {
    const tE = e.startsWith('input') ? 'input.push' : e;
    msg.payload.push({ 
        "id": 1, 
        "method": "Webhook.Create", 
        "params": { 
            "cid": msg.channelID, 
            "enable": true, 
            "event": e,
            "name": "wandschalter_" + e,
            "urls": [`${flow.get('webAppSwitchURL')}?channelID=${channelID}&style=${tE}`]
        }
    })
}

return msg;

msg.url = `${msg.Shelly._http_tcp.url}/rpc`;
msg.payload = [
    { "id": 1, "method": "Script.Create", "params": { "name": flow.get('scriptVersionName') } },
    { "id": 1, "method": "Script.PutCode", "params": { "id": 1, "code": `console.log('Wandschalter-Script: ${flow.get('scriptVersionName')}')` } },
]

return msg;

