return msg;

msg.url = `${msg.Shelly._http_tcp.url}/rpc`;
const result = msg.payload.result;
msg.payload = [];
if (result.scripts)
{
    for(const e of result.scripts){
        msg.payload.push({ "id": 1, "method": "Script.Delete", "params": { "id": e.id } })
    }
}
return msg;

msg.url = msg.payload.url + '/settings';
msg.payload = {}
return msg;

