msg.payload = {}
return msg;

msg.payload = flow.get('_http_tcp')[msg.topic]
msg._http_tcp = msg.payload
return msg;

const allShellies = flow.get('allShellies') ?? {};

allShellies[msg.payload.ip] = msg.payload;
flow.set('allShellies', allShellies);

return msg;

msg.url = `${msg.Shelly._http_tcp.url}/rpc/Switch.SetConfig?id=${msg.payload.id}&config={"in_mode":"detached","initial_state":"on","auto_on":false,"auto_off":false}`;
msg.channelID = msg.payload.id
return msg;

msg.url = `${msg.Shelly._http_tcp.url}/rpc/Switch.SetConfig?id=${msg.payload.id}&config={"in_mode":"flip","initial_state":"restore_last","auto_on":false,"auto_off":false}`;
msg.channelID = msg.payload.id
return msg;

if(!msg.hasOwnProperty('partSG')){
    msg.partSG = {
        length: msg.payload.length,
        correntIndex:0,
        array: msg.payload
    }
} else {
    msg.partSG.correntIndex = msg.partSG.correntIndex + 1;
}

if (msg.partSG.correntIndex === msg.partSG.length){
    delete msg.partSG;
    return [msg, null];
}

msg.payload = msg.partSG.array[msg.partSG.correntIndex];
return [null,msg];

const scriptVersionName = flow.get('scriptVersionName');

msg.payload.versionUpToDate = (msg.payload.gen2) 
    ? Object.entries(msg.payload.config)
        .filter(([key,]) => key.startsWith('script'))
        .filter(([key, value]) => value.name.startsWith('wandschalter_version_script_'))
        .some(([key, value]) => value.name === scriptVersionName)
    : true;

return msg;

for (const [key, value] of Object.entries(msg)){
    if(["_msgid", "payload", "topic"].includes(key)){
        continue;
    }
    flow.set(key,value);
}

flow.set('adapter', msg.adapterName);
flow.set('instance', `${msg.adapterName}.${msg.instanceNumber}`);
flow.set('device', `${msg.adapterName}.${msg.instanceNumber}.${msg.deviceName}`);
flow.set('mappingPath', `${msg.adapterName}.${msg.instanceNumber}.${msg.deviceName}.mapping`);
flow.set('infoPath', `${msg.adapterName}.${msg.instanceNumber}.${msg.deviceName}.info`);
flow.set('switchURL', `${msg.localServerAdapterURL}switch`)

return msg;

if(
    msg.command === 'init' && 
    msg.switches.includes('sonoff'))
    {
        return msg;  
    }
return;
return msg;

if(msg.payload && 
        Array.isArray(msg.payload) && 
        msg.payload.length > 0  && 
    msg.payload.some(e => e.hasOwnProperty('styleButtonIOBrokerStatePath') && e.styleButtonIOBrokerStatePath !== ''))
{
    msg.payload = msg.payload.filter(e => e.hasOwnProperty('styleButtonIOBrokerStatePath') && e.styleButtonIOBrokerStatePath !== '').map(mapping => {
        return {
            stateID: mapping['styleButtonIOBrokerStatePath'].split('&%&')[0],
            urls: [flow.get('switchURL')],
            forceOverwritte: false
        }
    })
    msg.url = flow.get('sgWebAPIURL') + 'iobroker/addURLNotificationSubscriptions'
    return [msg, null];
}
else{
    msg.payload = 'nothing to register'
    return [null,msg];
}

if(Array.isArray(msg.mappings) && msg.mappings.length > 0){
    if(msg.singleMapping.hasOwnProperty('styleButtonIOBrokerStatePath')){
        msg.payload = msg.mappings.find(e => e.styleButtonIOBrokerStatePath === msg.singleMapping.styleButtonIOBrokerStatePath);
        return msg
    }
    if (msg.singleMapping.hasOwnProperty('switchIOBrokerChannelPath') && msg.singleMapping.hasOwnProperty('style')){
        msg.payload = msg.mappings.find(e => e.switchIOBrokerChannelPath === msg.singleMapping.switchIOBrokerChannelPath && 
                            e.style ===  msg.singleMapping.style);
        return msg
    }
}
msg.payload = undefined
return msg;

if(!msg.statusCode || [200,201].includes(msg.statusCode)){
    node.status({
        "fill": "green",
        "shape": "ring",
        "text": 'all ok'
    })
    msg.payload = 'all ok';
} else {
    node.status({
        "fill": "red",
        "shape": "ring",
        "text": JSON.stringify(msg.payload) + `statusCode: ${msg.statusCode}`
    })
    node.error(JSON.stringify(msg.payload) + `statusCode: ${msg.statusCode}`);
    msg.error = JSON.stringify(msg.payload) + `statusCode: ${msg.statusCode}`;
    msg.payload = undefined;
}

return msg;

node.status({
        "fill": "green",
        "shape": "ring",
        "text": "init finished"
})
msg.payload = undefined;
return msg;

if(msg.error){
    node.status({
        "fill": "red",
        "shape": "ring",
        "text": `init finished with Error - ${msg.error}`
    })
} else {
    node.status({
        "fill": "green",
        "shape": "ring",
        "text": `init finished`
    })
}

if (msg.payload && typeof msg.payload === 'object' && msg.payload.hasOwnProperty('styleButtonIOBrokerStatePath') && msg.payload.styleButtonIOBrokerStatePath !== ''){
    msg.payload = [msg.payload];
}
if(msg.payload && 
        Array.isArray(msg.payload) && 
        msg.payload.length > 0  && 
        msg.payload.some(e => e.hasOwnProperty('styleButtonIOBrokerStatePath')))
{
    msg.payload = msg.payload.map(mapping => {
        return {
            stateID: mapping['styleButtonIOBrokerStatePath'].split('&%&')[0],
            urls: [flow.get('switchURL')],
        }
    })
    msg.url = flow.get('sgWebAPIURL') + 'iobroker/deleteURLNotificationSubscriptions'
    return [msg, null];
}
else{
    msg.payload = 'nothing to delete'
    return [null,msg];
}

if(!msg.statusCode || [200,201].includes(msg.statusCode)){
    node.status({
