        "text": JSON.stringify(msg.payload) + `statusCode: ${msg.statusCode}`
    })
    node.error(JSON.stringify(msg.payload) + `statusCode: ${msg.statusCode}`);
    msg.error = JSON.stringify(msg.payload) + `statusCode: ${msg.statusCode}`;
    msg.payload = undefined
}

return msg;

const newMappings = msg.mappings.filter(e => 
    !Object.entries(msg.singleDeleteMapping).every(([key,value]) => e.hasOwnProperty(key) && e[key] === value)
);
msg.payload = newMappings;
return msg;

if (msg.payload && typeof msg.payload === 'object' && msg.payload.hasOwnProperty('styleButtonIOBrokerStatePath') && msg.payload.styleButtonIOBrokerStatePath !== ''){

    msg.payload = {
        stateID: msg.payload['styleButtonIOBrokerStatePath'].split('&%&')[0],
        urls: [flow.get('switchURL')],
    }
    msg.url = flow.get('sgWebAPIURL') + 'iobroker/addURLNotificationSubscription'
    return [msg, null];
}
else{
    msg.payload = 'nothing to add'
    return [null,msg];
}

if(!msg.statusCode || [200,201].includes(msg.statusCode)){
    node.status({
        "fill": "green",
        "shape": "ring",
        "text": 'all ok'
    })
    msg.payload = 'all ok';
} else {
    node.status({
        "fill": "red",
        "shape": "ring",
        "text": JSON.stringify(msg.payload) + `statusCode: ${msg.statusCode}`
    })
    node.error(JSON.stringify(msg.payload) + `statusCode: ${msg.statusCode}`);
    msg.error = JSON.stringify(msg.payload) + `statusCode: ${msg.statusCode}`;
    msg.payload = undefined;
}

return msg;

node.status({
        "fill": "green",
        "shape": "ring",
        "text": "added"
})
msg.payload = undefined;
return msg;

node.status({
        "fill": "green",
        "shape": "ring",
        "text": "changed"
})
msg.payload = undefined;
return msg;

node.status({
        "fill": "red",
        "shape": "ring",
        "text": "not changed"
})
msg.payload = undefined;
return msg;

// consolidate switch data

const consumerData = {}
msg.payload.forEach(e => {
    Object.entries(e).forEach(([key,value]) => {
        consumerData[key] = value;
    })
})

flow.set(`${msg.ConfigFunctionName}_ConsumersList`, consumerData)
msg.payload = {}
msg.consumersList = consumerData;

return msg;

// consolidate switch data

const switchData = {}
msg.payload.forEach(e => {
    Object.entries(e).forEach(([key,value]) => {
        switchData[key] = value;
    })
})

flow.set(`${msg.ConfigFunctionName}_SwitchesList`, switchData)
msg.payload = {}
msg.switchesList = switchData

return msg;

// check mapping configration
if(
        msg.ConfigFunctionName && 
        msg.filter && 
        msg.switches && 
        msg.consumers)
    {
        msg.command = 'init';
        msg.payload = msg.switches;
        node.status({fill:"green",shape:"dot",text:"ok"});
    }else{
        node.status({fill:"red",shape:"ring",text:"nok"});
        msg = undefined
    }
const {ConfigFunctionName, filter, switches, consumers} = msg
return [{payload:{ConfigFunctionName, filter, switches, consumers}}, msg];

const pl = msg.payload;
msg.payload = true;


// check if object
if(typeof pl !== 'object'){
    msg.payload = false;
}

// check if all values are strings
if (!Object.values(pl).every(e => typeof e === 'string')) {
    msg.payload = false;
}

// check if all keys are valid
if(!Object.keys(pl).every(e => 
        [
            'switchIOBrokerChannelPath', 
            'styleButtonIOBrokerStatePath', 
            'style', 
            'consumerIOBrokerChannelPath', 
            'consumerIOBrokerStatePath',
            'configType'
    ].includes(e))) {
    msg.payload = false;
}

// check all mandatory keys
if (!(pl.hasOwnProperty('switchIOBrokerChannelPath') && pl.hasOwnProperty('consumerIOBrokerChannelPath') && pl.hasOwnProperty('configType'))) {
    msg.payload = false;
}

// check mandatory key variation
if(!((pl.hasOwnProperty('style') && pl.configType ==='wandschalterConfig' )|| 
    (pl.hasOwnProperty('styleButtonIOBrokerStatePath') && pl.configType === 'mobilerschalterConfig'))) {
    msg.payload = false;
}

// if new some additional checks
if(msg.isNewMapping === true){
    const mappings = flow.get('mappings')
    if(pl.hasOwnProperty('styleButtonIOBrokerStatePath')){
        if (mappings.some(e => e.styleButtonIOBrokerStatePath !== '' && e.styleButtonIOBrokerStatePath === pl.styleButtonIOBrokerStatePath && e.switchIOBrokerChannelPath === pl.switchIOBrokerChannelPath)){
            msg.payload = false;
            msg.error = 'mapping already available';
        }
    }
    if(pl.hasOwnProperty('style')){
        if (mappings.some(e => e.style !== '' && e.style === pl.style && e.switchIOBrokerChannelPath === pl.switchIOBrokerChannelPath)){
            msg.payload = false;
            msg.error = 'mapping already available';
        }
    }

}

if(msg.payload === false && !msg.error){
    msg.error = 'not a valid mapping object'
}

return msg;

node.status({
        "fill": "red",
        "shape": "ring",
        "text": "not changed, because mapping is not available"
})
msg.payload = undefined;
return msg;

msg.payload.switchesList = flow.get(`${msg.payload.ConfigFunctionName}_SwitchesList`);
msg.payload.consumersList = flow.get(`${msg.payload.ConfigFunctionName}_ConsumersList`);
return msg;

msg.payload = msg.payload.map(e => {
    Object.entries(e).forEach(([key, value]) => {
        if(value === '') delete e[key]
    })
    return e
})
return msg;
